package utils

import org.junit.runner.RunWith
import org.scalatest.junit.JUnitRunner
import org.scalatest.FunSuite
import utils.DNAUtils._

@RunWith(classOf[JUnitRunner])
class DNAUtilsSuite extends FunSuite {

  test("Basic DNA Reversal") {
    assert(reverseComplement("AAAACCCGGT") === "ACCGGGTTTT")
  }

  test("Assignment DNA Reveral") {
    assert(reverseComplement("") === "")

  }

  test("initial Value") {
    assert(initialValue(5) === "AAAAA")
  }

  test("next Value") {
    assert(nextValue("AAA") === "TAA")
    assert(nextValue("TAA") === "GAA")
    assert(nextValue("GAA") === "CAA")
    assert(nextValue("CAA") === "ATA")
    assert(nextValue("CCC") === "AAA")
  }

}